sys_variable_value.value:
(function(outputs, steps, params, stepResult, assertEqual) {

    describe("RequestBuilder::generateRestMessage Unit Tests", function() {

        it("should construct a RESTMessageV2 object", function() {
            var testEndpoint = "http://example.com";
            var testMethod = "GET";

            var request = new sn_auth.HttpRequestData();
            request.setEndpoint(testEndpoint);
            request.setHttpMethod(testMethod);

            var creds = new sn_auth.HttpRequestAuthedData();
            creds.setDirective("header");
            creds.addHeader("Authorization", "Basic 323e9fsafiefwa");

            var builder = new x_neyu_rest_utils.RequestBuilder();
            var spy = spyOn(builder, "getMidForConnection");
            spy.and.returnValue(null);

            var restMessage = builder.generateRestMessage(request, creds);

            expect(restMessage).toBeDefined();
            expect(restMessage.getEndpoint()).toBe(testEndpoint);
            expect(restMessage.getRequestHeaders()["Authorization"]).toEqual("Basic 323e9fsafiefwa");
        });

        it("Should add queryParams to the endpoint", function() {
            var testEndpoint = "http://example.com";
            var testMethod = "GET";
            var queryParams = {
                q: "1234",
                a: "4321"
            };

            var request = new sn_auth.HttpRequestData();
            request.setEndpoint(testEndpoint);
            request.setHttpMethod(testMethod);
            Object.keys(queryParams).forEach(function(key) {
                request.addQueryParam(key, queryParams[key]);
            });

            var creds = new sn_auth.HttpRequestAuthedData();
            creds.setDirective("header");
            creds.addHeader("Authorization", "Basic 323e9fsafiefwa");

            var builder = new x_neyu_rest_utils.RequestBuilder();
            var spy = spyOn(builder, "getMidForConnection");
            spy.and.returnValue(null);

            var restMessage = builder.generateRestMessage(request, creds);

            expect(restMessage).toBeDefined();
            expect(restMessage.getEndpoint()).toContain(testEndpoint);
            expect(restMessage.getEndpoint()).toContain("?");
            expect(restMessage.getEndpoint()).toContain("q=1234");
            expect(restMessage.getEndpoint()).toContain("a=4321");
            expect(restMessage.getRequestHeaders()["Authorization"]).toEqual("Basic 323e9fsafiefwa");
        });

        it("Should add headers to the restmessage if provided", function() {
            var testEndpoint = "http://example.com";
            var testMethod = "GET";
            var testHeaders = {
                "Content-Type": "application/json",
                "Accept": "application/json"
            };

            var request = new sn_auth.HttpRequestData();
            request.setEndpoint(testEndpoint);
            request.setHttpMethod(testMethod);
            Object.keys(testHeaders).forEach(function(key) {
                request.addHeader(key, testHeaders[key]);
            });

            var creds = new sn_auth.HttpRequestAuthedData();
            creds.setDirective("header");
            creds.addHeader("Authorization", "Basic 323e9fsafiefwa");

            var builder = new x_neyu_rest_utils.RequestBuilder();
            var spy = spyOn(builder, "getMidForConnection");
            spy.and.returnValue(null);

            var restMessage = builder.generateRestMessage(request, creds);

            expect(restMessage).toBeDefined();
            expect(restMessage.getEndpoint()).toBe(testEndpoint);
            expect(restMessage.getRequestHeaders()["Authorization"]).toBe("Basic 323e9fsafiefwa");
            expect(restMessage.getRequestHeaders()["Content-Type"]).toBe("application/json");
            expect(restMessage.getRequestHeaders()["Accept"]).toBe("application/json");
        });

        it("Should add content to the RESTMessageV2 if content is provided", function() {
            var testEndpoint = "http://example.com";
            var testMethod = "POST";
            var testContent = JSON.stringify({
                "approved": "A"
            });

            var request = new sn_auth.HttpRequestData();
            request.setEndpoint(testEndpoint);
            request.setHttpMethod(testMethod);
            request.setContent(testContent);

            var creds = new sn_auth.HttpRequestAuthedData();
            creds.setDirective("header");
            creds.addHeader("Authorization", "Basic 323e9fsafiefwa");

            var builder = new x_neyu_rest_utils.RequestBuilder();
            var spy = spyOn(builder, "getMidForConnection");
            spy.and.returnValue(null);

            var restMessage = builder.generateRestMessage(request, creds);
            expect(restMessage).toBeDefined();
            expect(restMessage.getEndpoint()).toBe(testEndpoint);
            expect(restMessage.getRequestBody()).toBe(testContent);
        });

        it("Should NOT add content to the RESTMessageV2 if no content is provided", function() {
            var testEndpoint = "http://example.com";
            var testMethod = "POST";

            var request = new sn_auth.HttpRequestData();
            request.setEndpoint(testEndpoint);
            request.setHttpMethod(testMethod);

            var creds = new sn_auth.HttpRequestAuthedData();
            creds.setDirective("header");
            creds.addHeader("Authorization", "Basic 323e9fsafiefwa");

            var builder = new x_neyu_rest_utils.RequestBuilder();
            var spy = spyOn(builder, "getMidForConnection");
            spy.and.returnValue(null);

            var restMessage = builder.generateRestMessage(request, creds);
            expect(restMessage).toBeDefined();
            expect(restMessage.getEndpoint()).toBe(testEndpoint);
            expect(restMessage.getRequestBody()).toBe(null);
        });

    });

})(outputs, steps, params, stepResult, assertEqual);
// uncomment the next line to execute this script as a jasmine test
jasmine.getEnv().execute();
