sys_variable_value.value:
(function(outputs, steps, params, stepResult, assertEqual) {

	describe("REST Utils RequestBuilder getMidForConnection Unit Tests", function() {

		it("getMidForConnection should not call _getCapableMidServer if connectionInfo is null", function() {
            var builder = new x_neyu_rest_utils.RequestBuilder();
            var spy = spyOn(builder, "_getCapableMidServer");
            var mid = builder.getMidForConnection();

            expect(spy).not.toHaveBeenCalled();
            //expect(mid).not.toBeDefined();
        });

        it("getMidForConnection should not call _getCapableMidServer if use_mid is false", function() {
            var builder = new x_neyu_rest_utils.RequestBuilder();
            var spy = spyOn(builder, "_getCapableMidServer");
            var mid = builder.getMidForConnection({
				getAttribute: function(name) {
					return false;
				}
            });

            expect(spy).not.toHaveBeenCalled();
            expect(mid).toBe("");
        });

        it("getMidForConnection should return a mid name if use_mid is true", function() {
            var builder = new x_neyu_rest_utils.RequestBuilder();
            var spy = spyOn(builder, "_getCapableMidServer");
            var fakeMidName = "A-MID-SERVER";
            spy.and.returnValue(fakeMidName);
            var mid = builder.getMidForConnection({
				getAttribute: function(name) {
					return true;
				},
				getCredentialAttribute: function(attr) {
					if (attr === "applies_to") {
						return "REST";
					}
					if (attr === "mid_list") {
						return "";
					}
				}
            });

            expect(spy).toHaveBeenCalled();
			expect(spy).toHaveBeenCalledWith("REST", "");
            expect(mid).toBe(fakeMidName);
        });

	});

})(outputs, steps, params, stepResult, assertEqual);
// uncomment the next line to execute this script as a jasmine test
jasmine.getEnv().execute();
